let unix_timestamp = msg.payload.time;
global.set("g_currenttime",unix_timestamp);
// Create a new JavaScript Date object based on the timestamp
// multiplied by 1000 so that the argument is in milliseconds, not seconds
var date = new Date(unix_timestamp);

var year = date.getFullYear();
var month = date.getMonth()+1;
var day = date.getDate();
var hours = date.getHours();
var minutes = "0" + date.getMinutes();
var seconds = "0" + date.getSeconds();

var formattedTime = year + '-';
formattedTime += (month<10)?'0':'';
formattedTime += month + '-';
formattedTime += (day<10)?'0':'';
formattedTime += day + ' ';
formattedTime += (hours<10)?'0':'';
formattedTime += hours + ':';
formattedTime += (minutes.substr(-2)<10)?'0':'';
formattedTime += minutes.substr(-2) + ':' ;
formattedTime += (seconds.substr(-2)<10)?'0':'';
formattedTime += seconds.substr(-2);

node.status({fill:"green",text:formattedTime});
msg.payload = formattedTime;
return msg;

var starttime = flow.get("teststarttime");
var currtime = msg.payload.time;
var runtime = (currtime - starttime) / 1000;
var remainder = 0;
var days = Math.floor(runtime / (60 * 60 * 24));
if (days == 0) {
    remainder = runtime;
} else {
    remainder = runtime % days;
}
var hours = Math.floor(remainder / (60 * 60));
if (hours == 0) {
    remainder = runtime;
} else {
    remainder = runtime % hours;
}
var minutes = Math.floor(remainder / (60));
if (minutes == 0) {
    remainder = runtime;
} else {
    remainder = runtime % minutes;
}
var seconds = Math.floor(remainder);
var formattedTime = days+"d "+hours+"h "+minutes+"m "+seconds+"s";
msg.payload = formattedTime;
return msg;

