// It's total number of characters is at least the absolute value of the width
// If width is a positive number, then the string is padded to the right
// If negative, it is padded to the left
// char argument specifies the padding character, default is space
const expression = [
    jsonata("$pad(example, 7)"),
    jsonata("$pad(example, -7)"),
